const { Argument } = require('commander/lib/argument');
const { Command } = require('commander/lib/command');
const { CommanderError, InvalidArgumentError } = require('commander/lib/error');
const { Help } = require('commander/lib/help');
const { Option } = require('commander/lib/option');

// @ts-check

/**
 * Expose the root command.
 */

exports = module.exports = new Command();
exports.program = exports; // More explicit access to global command.
// Implicit export of createArgument, createCommand, and createOption.

/**
 * Expose classes
 */

exports.Argument = Argument;
exports.Command = Command;
exports.CommanderError = CommanderError;
exports.Help = Help;
exports.InvalidArgumentError = InvalidArgumentError;
exports.InvalidOptionArgumentError = InvalidArgumentError; // Deprecated
exports.Option = Option;
