'use strict';

var define = require('define-properties');
var callBind = require('call-bind');

var implementation = require('regexp.prototype.flags/implementation');
var getPolyfill = require('regexp.prototype.flags/polyfill');
var shim = require('regexp.prototype.flags/shim');

var flagsBound = callBind(getPolyfill());

define(flagsBound, {
	getPolyfill: getPolyfill,
	implementation: implementation,
	shim: shim
});

module.exports = flagsBound;
